mapper.select:

WHERE j.role = #{role}
  AND j.environment = #{environment}
  AND j.name = #{name}

SELECT c.id AS id FROM task_configs AS c INNER JOIN tasks AS t ON t.task_config_row_id = c.id WHERE t.task_id IN (
#{taskId}
)

mapper.insert:
